mapper.select:
select MENU_CODE, MENU_TITLE, MENU_GNB
FROM MENU
WHERE MENU_CODE = #{value}

mapper.update:
UPDATE MENU
   SET MENU_SEQ = #{MENU_SEQ}
      ,MENU_TITLE = #{MENU_TITLE}
      ,MENU_URL = #{MENU_URL}
      ,MENU_GNB = #{MENU_GNB}
      ,MENU_PARENT = #{MENU_PARENT}
      ,MENU_LEVEL = #{MENU_LEVEL}
      ,MENU_CHILD_YN = #{MENU_CHILD_YN}
      ,DEL_YN = #{DEL_YN}
WHERE MENU_CODE = #{MENU_CODE}
